<template>        <div>            <md-table  md-height="800px" class="my-table" v-model="entries" v-if="count" md-card md-fixed-header>                <md-table-toolbar>                    <md-badge  class="md-square md-primary" :md-content="count">                        <h3 class="md-title"  style="flex: 1" ><font-awesome-icon icon="user" />   Individuals  </h3>                    </md-badge>                    <md-button :href="resource_url" title="JSON" target="_blank" class="md-fab md-mini">                            <font-awesome-icon icon="code"/>                    </md-button>                </md-table-toolbar>                <md-table-row slot="md-table-row" slot-scope="{item}">                    <md-table-cell md-label="Info"> <p> <span class="key"> Name  </span> {{ item.name }} </p> <p> <span class="key"> Study </span> {{item.study_name}}</p></md-table-cell>                   <md-table-cell md-label="Characteristica">                       <div class="my-cell">                           <CharacteristicaChip  class="item" v-for="(c, index1) in item.all_characteristica_final" :key="index1" :api="api" :id="id_from_url(c)"/>                       </div>                   </md-table-cell>               </md-table-row>            </md-table>           <v-paginator :resource_url="resource_url"  @update="updateResource" config=""></v-paginator>       </div></template><script>import VuePaginator from '@/components/VPaginator';import CharacteristicaChip from './CharacteristicaChip';import {id_from_url} from "./utils";export default {   name: 'Individuals',   components: {       VPaginator: VuePaginator,       CharacteristicaChip: CharacteristicaChip,   },   props: {       api: String   },   methods:{       updateResource(data){           this.entries = data.data;           this.count = data.count       },       id_from_url(url){           return id_from_url(url);       }   },   data() {       return {           entries: [],           count: null,           resource_url: this.api + '/individuals_read/?format=json',           }       },}</script><style>    .my-table{        table-layout: fixed;        width: inherit;    }    .my-cell {        display:block;        white-space: nowrap;        height: 105px;        padding-left: 2px;    }    .item {        display: inline-block;    }    .key{        opacity: .54;        font-size: 14px;        letter-spacing: .01em;        line-height: 20px;    }</style>