<template>    <div>        <slot :resource_url="resource_url" :response="response" :loaded="loaded" :errors="errors"></slot>    </div></template><script>    /**     * Loads data from resource url and injects in slot.     */    import axios from 'axios'    export default {        name: 'GetData',        props: {            resource_url: {                type: String,  // endpoint for REST query                required: true            }        },        data() {            return {                response: {},                loaded : false,                errors: {},            }        },        methods: {            fetch_data(url){               axios.get(url)                .then(response => {                    this.response = response;                    this.loaded = true;                })               .catch((error)=>{                   this.response = null;                   console.error(this.resource_url);                   console.error(error);                   this.errors = error.response.data;               })            },        },        mounted() {            this.fetch_data(this.resource_url);        },    }</script><style></style>