<template>    <div>        <slot :data="data" :loaded="loaded">        </slot>    </div></template><script>    import axios from 'axios'    export default {        name: 'GetData',        props: {            api_url: String        },        data() {            return {                data: {},                loaded : false,                errors: {},            }        },        mounted() {            // console.log(this.api_url);            this.fetch_data(this.api_url);        },        methods: {            fetch_data(url){               axios.get(url)                .then(response => {                    this.data = response.data;                    this.loaded = true;                })            },        }    }</script><style></style>