<template>    <div>        <slot :data="data" :loaded="loaded" :errors="errors"></slot>    </div></template><script>    /**     * Loads data from resource url and injects in slot.     */    import axios from 'axios'    export default {        name: 'GetData',        props: {            api_url: String  // endpoint for REST query        },        data() {            return {                data: {},                loaded : false,                errors: {},            }        },        mounted() {            // console.log(this.api_url);            this.fetch_data(this.api_url);        },        methods: {            fetch_data(url){               axios.get(url)                .then(response => {                    this.data = response.data;                    this.loaded = true;                })               .catch((error)=>{                   this.errors = error.response.data;                   console.log(error);               })            },        }    }</script><style></style>