<template>    <span id="get-paginated-data">        <slot :entries="entries" :count="count"></slot>        <v-paginator :resource_url="resource_url" @update="updateResource" config=""></v-paginator>    </span></template><script>    /**     * Loads data (entries & count) from given resource url.     */    import VuePaginator from './VPaginator';    export default {        name: 'GetPaginatedData',        components: {            VPaginator: VuePaginator,        },        props: {            resource_url: {                type: String,                required: true            },        },        methods: {            updateResource(data) {                this.entries = data.data;                this.count = data.count            }        },        data() {            return {                entries: [],                count: null,            }        },    }</script><style></style>