<template>    <v-card>        <v-card-title>            <Heading :count="data.count" :icon="icon('group')" title="Groups" :resource_url="resource_url"/>        </v-card-title>        <v-data-table                :headers="headers"                :items="data.entries"                hide-actions                class="elevation-1">            <template slot="items" slot-scope="table">                <td>                    <LinkButton :to="'/groups/'+ table.item.pk" :title="'Group: '+table.item.pk" :icon="icon('group')"/>                    <JsonButton :resource_url="api + '/groups_read/'+ table.item.pk +'/?format=json'"/>                </td>                <td>                    <GroupInfo :group="table.item"/>                </td>                <td>                    <CharacteristicaCard class="item" v-for="(c, index1) in table.item.characteristica_all_final"                                         :key="index1" :id="id_from_url(c)"/>                </td>            </template>        </v-data-table>    </v-card></template><script>    import Table from '@/components/tables/Table'    import Individuals from '@/components/Individuals'    import CharacteristicaCard from '@/components/detail/CharacteristicaCard';    import {lookup_icon} from "@/icons"    import {id_from_url} from "@/utils";    export default {        name: 'GroupsTable',        components: {            CharacteristicaCard: CharacteristicaCard,            Individuals: Individuals,            Table: Table,        },        props: {            data: Object,            resource_url: String,        },        data() {            return {                headers: [                    {text: 'Group', value: 'group'},                    {text: 'Info', value: 'info'},                    {text: 'Characteristica', value: 'characteristica'},                ],            }        },        computed: {            api() {                return this.$store.state.endpoints.api;            }        },        methods: {            icon: function (key) {                return lookup_icon(key)            },            id_from_url(url){                return id_from_url(url);            },        }    }</script><style></style>