<template>    <v-card>        <v-card-title>            <Heading :count="data.count" :icon="icon('group')" title="Groups" :resource_url="resource_url"/>        </v-card-title>        <v-data-table                :headers="headers"                :items="data.entries"                hide-actions                class="elevation-1">            <template slot="items" slot-scope="table">                <td>                    <LinkButton :to="'/groups/'+ table.item.pk" :title="'Group: '+table.item.pk" :icon="icon('group')"/>                    <JsonButton :resource_url="api + '/groups_read/'+ table.item.pk +'/?format=json'"/>                </td>                <td>                    <GroupInfo :group="table.item"/>                </td>                <td>                    <span v-for="(c_url, index1) in table.item.characteristica_all_final" :key="index1">                            <GetData :resource_url="c_url">                                <div slot-scope="cdata">                                    <CharacteristicaCard :data="response" :resource_url="resource_url"/>                                </div>                            </GetData>                        <GetData>                        </GetData>                    </span>                </td>            </template>        </v-data-table>    </v-card></template><script>    import {lookup_icon} from "@/icons"    import {id_from_url} from "@/utils";    import Table from './Table'    import Individuals from '../Individuals'    import CharacteristicaCard from '../detail/CharacteristicaCard';    export default {        name: 'GroupsTable',        components: {            CharacteristicaCard,            Individuals,            Table,        },        props: {            data: Object,            resource_url: String,        },        data() {            return {                headers: [                    {text: 'Group', value: 'group'},                    {text: 'Info', value: 'info'},                    {text: 'Characteristica', value: 'characteristica'},                ],            }        },        computed: {            api() {                return this.$store.state.endpoints.api;            }        },        methods: {            icon: function (key) {                return lookup_icon(key)            },            id_from_url(url){                return id_from_url(url);            },        }    }</script><style></style>