<template>    <div class="groups-table">        <heading-toolbar :count="data.count" :icon="icon('group')" title="Groups" :resource_url="resource_url"/>        <v-card>        <v-data-table                :headers="headers"                :items="data.entries"                hide-actions                class="elevation-1">            <template slot="items" slot-scope="table">                <td>                    <link-button :to="'/groups/'+ table.item.pk" :title="'Group: '+table.item.pk" :icon="icon('group')"/>                    <json-button :resource_url="api + '/groups_read/'+ table.item.pk +'/?format=json'"/>                </td>                <td>                    <group-info :group="table.item"/>                </td>                <td>                   <span v-for="(c_url, index1) in table.item.characteristica_all_final" :key="index1">                    <get-data :resource_url="c_url">                        <div slot-scope="cdata">                            <characteristica-card :data="cdata.data" :resource_url="c_url"/>                        </div>                    </get-data>                    </span>                </td>            </template>        </v-data-table>    </v-card>    </div></template><script>    import {lookup_icon} from "@/icons"    import CharacteristicaCard from '../detail/CharacteristicaCard';    export default {        name: 'GroupsTable',        components: {            CharacteristicaCard,        },        props: {            data: Object,            resource_url: String,        },        data() {            return {                headers: [                    {text: 'Group', value: 'group'},                    {text: 'Info', value: 'info'},                    {text: 'Characteristica', value: 'characteristica'},                ],            }        },        computed: {            api() {                return this.$store.state.endpoints.api;            }        },        methods: {            icon: function (key) {                return lookup_icon(key)            },        }    }</script><style></style>