<template>    <div>        <Table :fixed_header="fixed_header" :resource="resource"  :resource_url="resource_url" :comments="comments" :descriptions="descriptions">            <template slot="title">                <font-awesome-icon icon="users" />   Groups            </template>            <template slot="tableRow" slot-scope="table">                <md-table-cell md-label="Info">                    <div class="my-info">                        <div class="item">                            <md-list class="md-double-line">                                <div class="md-list-item-text">                                    <span>{{ table.item.name }}</span>                                    <span>Name</span>                                </div>                                <div class="md-list-item-text">                                    <span>{{ table.item.count }}</span>                                    <span>Count</span>                                </div>                                <div class="md-list-item-text">                                    <span>{{ table.item.study_name }}</span>                                    <span>Study</span>                                </div>                            </md-list>                        </div>                        <div class="item">                            <md-badge title="Individuals" v-if="table.item.individuals.length" class="md-square md-primary " :md-content="table.item.individuals.length">                                <md-button @click="showDialog = true" class="md-icon-button md-raised">                                    <font-awesome-icon icon="user" />                                </md-button>                            </md-badge>                            <md-dialog :md-active.sync="showDialog">                                <md-dialog-content>                                    <Individuals />                                </md-dialog-content>                            </md-dialog>                            <md-button :href="api + '/groups_read/'+ table.item.pk +'/?format=json'" title="JSON" target="_blank" class="md-icon-button md-raised" >                                <font-awesome-icon icon="code"/>                            </md-button>                        </div>                    </div>                </md-table-cell>                <md-table-cell md-label="Characteristica">                    <div class="my-cell">                        <CharacteristicaCard class="item" v-for="(c, index1) in table.item.characteristica_all_final" :key="index1"  :id="id_from_url(c)"/>                    </div>                </md-table-cell>            </template>        </Table>    </div></template><script>    import Table from '@/components/tables/Table'    import Individuals from '@/components/Individuals'    import CharacteristicaCard from '@/components/detail/CharacteristicaCard';    import {id_from_url} from "@/utils";    export default {        name: 'GroupsTable',        components: {            CharacteristicaCard: CharacteristicaCard,            Individuals:Individuals,            Table:Table,        },        props: {            resource: Object,            descriptions:Array,            comments:Array,            resource_url:String,        },        methods:{            id_from_url(url){                return id_from_url(url);            }        },        data() {            return {                showDialog: false,            }        },        computed: {         // vuex store            api() {                    return this.$store.state.endpoints.api;                },            fixed_header(){                if (this.resource.count > 10) {                    return true;                }            }        }    }</script><style></style>