<template>    <Table :data="data" :resource_url="resource_url" title="Groups" icon="users">        <template slot="row" slot-scope="table">            <md-table-cell md-label="Info">                <md-list class="md-double-line">                    <div class="md-list-item-text">                        <span>{{ table.item.name }}</span>                        <span>Name</span>                    </div>                    <div class="md-list-item-text">                        <span>{{ table.item.count }}</span>                        <span>Count</span>                    </div>                    <div class="md-list-item-text">                        <span>{{ table.item.study_name }}</span>                        <span>Study</span>                    </div>                </md-list>                <md-badge title="Individuals" v-if="table.item.individuals.length" :md-content="table.item.individuals.length">                        <font-awesome-icon icon="user"/>                </md-badge>                <JsonButton :resource_url="api + '/groups_read/'+ table.item.pk +'/?format=json'"/>            </md-table-cell>            <md-table-cell md-label="Characteristica">                <div class="my-cell">                    <CharacteristicaCard class="item" v-for="(c, index1) in table.item.characteristica_all_final"                                         :key="index1" :id="id_from_url(c)"/>                </div>            </md-table-cell>        </template>    </Table></template><script>    import Table from '@/components/tables/Table'    import Individuals from '@/components/Individuals'    import CharacteristicaCard from '@/components/detail/CharacteristicaCard';    import {id_from_url} from "@/utils";    export default {        name: 'GroupsTable',        components: {            CharacteristicaCard: CharacteristicaCard,            Individuals: Individuals,            Table: Table,        },        props: {            data: Object,            resource_url: String,        },        methods:{            id_from_url(url){                return id_from_url(url);            },        },        data() {            return {            }        },        computed: {            api() {                return this.$store.state.endpoints.api;            }        }    }</script><style></style>