<template>    <div>        <template>            <div>                <Table fixed_header :resource="resource"  :resource_url="resource_url" :comments="comments" :descriptions="descriptions">                    <template slot="title">                        <font-awesome-icon icon="user" /> Individuals                    </template>                    <template slot="tableRow" slot-scope="table">                        <md-table-cell md-label="Info"> <p> <span class="key"> Name  </span> {{ table.item.name }} </p> <p> <span class="key"> Study </span> {{table.item.study_name}}</p></md-table-cell>                        <md-table-cell md-label="Characteristica">                            <div class="my-cell">                                <CharacteristicaCard  class="item" v-for="(c, index1) in table.item.all_characteristica_final" :key="index1"  :id="id_from_url(c)"/>                            </div>                        </md-table-cell>                    </template>                </Table>            </div>        </template>    </div></template><script>    import Table from '@/components/tables/Table'    import CharacteristicaCard from '@/components/detail/CharacteristicaCard';    import {id_from_url} from "@/utils";    export default {       name: 'IndividualsTable',       components: {           CharacteristicaCard: CharacteristicaCard,           Table:Table,       },       props: {           resource: Object,           resource_url:String,           descriptions:Array,           comments:Array,       },       methods:{           id_from_url(url){               return id_from_url(url);           },       },       computed: { // vuex store           api() {                return this.$store.state.endpoints.api;           },       }    }</script><style>    .my-cell {        display:block;        white-space: nowrap;        height: 105px;        padding-left: 2px;    }    .item {        display: inline-block;    }    .key{        opacity: .54;        font-size: 14px;        letter-spacing: .01em;        line-height: 20px;    }</style>