<template>    <div id="individuals-table">    <Heading :count="data.count" :icon="icon('individuals')" title="Individuals" :resource_url="resource_url"/>    <v-card>        <v-data-table                :headers="headers"                :items="data.entries"                hide-actions                class="elevation-1">            <template slot="items" slot-scope="table">                <td>                    <LinkButton :to="'/individuals/'+ table.item.pk" :title="'Individual: '+table.item.pk" :icon="icon('individual')"/>                    <JsonButton :resource_url="api + '/individuals_read/'+ table.item.pk +'/?format=json'"/>                </td>                <td>                    <IndividualInfo :individual="table.item"/>                </td>                <td>                    <get-data :resource_url="get_characterica_url(table.item.characteristica_all_final)">                        <div slot-scope="cdata">                            <span v-for="item in cdata.data.results">                                <characteristica-card :data="item" :resource_url="get_characterica_url(table.item.characteristica_all_final)" />                            </span>                        </div>                    </get-data>                </td>            </template>        </v-data-table>    </v-card>    </div></template><script>    import {lookup_icon} from "@/icons"    import CharacteristicaCard from '../detail/CharacteristicaCard';    export default {       name: 'IndividualsTable',       components: {           CharacteristicaCard,       },        props: {            data: Object,            resource_url: String,        },        data() {            return {                headers: [                    {text: 'Individual', value: 'individual'},                    {text: 'Info', value: 'info'},                    {text: 'Characteristica', value: 'characteristica'},                ],            }        },        computed: {            api() {                return this.$store.state.endpoints.api;            }        },        methods: {            get_characterica_url(ids){                return this.$store.state.endpoints.api + '/characteristica_elastic/?ids='+ ids.join('__')},            icon: function (key) {                return lookup_icon(key)            },        }    }</script><style></style>