<template>    <div>        <md-table :md-fixed-header="fixed_header" md-height="800px" class="my-table" v-model="resource.entries"                  v-if="resource.count">            <!-- Descriptions and comments -->            <md-table-toolbar>                <md-badge class="md-square md-primary" :md-content="resource.count">                    <h3 class="md-title" style="flex: 1">                        <slot name="title"></slot>                    </h3>                </md-badge>                <md-button :href="resource_url" title="JSON" target="_blank" class="md-icon-button"                           v-if="resource_url"><font-awesome-icon icon="code"/>                </md-button>                <span v-if="comments">                        <md-chip v-for="comment in comments" :key="comment">                            <md-avatar class="md-avatar-icon md-small">user:todo</md-avatar>                            {{comment}}                        </md-chip>                </span>                <ul v-if="descriptions">                    <li v-for="description in descriptions" :key="description">                        <span>{{description.text}}</span>                    </li>                </ul>            </md-table-toolbar>            <md-table-row slot="md-table-row" slot-scope="{item}">                <slot name="tableRow" :item="item"></slot>            </md-table-row>        </md-table>    </div></template><script>    import CharacteristicaChip from '../CharacteristicaChip';    import {id_from_url} from "../utils";    export default {       name: 'Table',       components: {           CharacteristicaChip: CharacteristicaChip,       },       props: {           resource: Object,           api:String,           resource_url:String,           fixed_header:Boolean,           descriptions:Array,           comments:Array,       },       methods:{           id_from_url(url){               return id_from_url(url);           }       },    }</script><style>    .my-table{        table-layout: fixed;        width: inherit;    }    .my-cell {        display:block;        white-space: nowrap;        height: 105px;        padding-left: 2px;    }    .my-info {        padding-left: 2px;        white-space: nowrap;        width: 156px;        display: flex;        justify-content: space-between;    }    .item {        display: inline-block;    }</style>